        public int getVal(int[] next,int sub){
                int index = next[sub];
                index = index/Main.smallSize;
                if(Main.pageList.containsKey(index)){
                        return 0;
                }else{
                        if(pageNum<Main.smallNum) {
                                Main.pageList.put(index, pageNum);
                                page_index[pageNum] = index;
                                pageNum++;
                                return 1;
                        }else{
                                int max_page = 0;
                                int max_dis = -1;
                                for(int i = 0;i<Main.smallNum;i++){
                                        int tem = page_index[i];
                                        for(int j = sub+1;j<next.length;j++){
                                                page_dis[i] = j-sub;
                                                if((next[j]/Main.smallSize) == tem){
                                                        break;
                                                }
                                        }
                                        if(page_dis[i]>max_dis){
                                                max_dis = page_dis[i];
                                                max_page = i;
                                        }
                                }
                                Main.pageList.remove(page_index[max_page]);
                                Main.pageList.put(index,max_page);
                                page_index[max_page] = index;
                                return 1;
                        }
                }
        }



